/// <reference types="vite/client" />

// Declare module for media files to make TypeScript happy
declare module '*.mp3' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*.svg' {
  const src: string;
  export default src;
}
